grammar.value:
root $input;
$input = $tod {$ = "deaths"} | $infektionen {$ = "cases"};
$tod = tod | gestorben | todeszahlen | todesfälle | vestorben | verstorbene;
$infektionen = fälle | infektionen | infektionszahlen | infizierte | infizierten | fallzahlen;

root $input;
$input = $alle {$ = "all"} | $woche {$ = "week"};
$alle = gesamt | gesamter zeitraum | gesamten zeitraum | bisher | bisherige | seit beginn;
$woche = sieben tage | woche;

root $input;
$input = $gesamt {$ = "all"} | $einwohnerzahl {$ = "pop"} | $einhundertk {$ = "hundredk"};
$gesamt = alle | anzahl| insgesamt | absolut | keine relation | summe;
$einwohnerzahl = einwohner | einwohnerzahl;
$einhundertk = hunderttausend | einhunderttausend;

root $input;
$input = $tod {$ = "deaths"} | $infektionen {$ = "cases"};
$tod = tod | gestorben | todeszahlen | todesfälle | vestorben | verstorbene;
$infektionen = fälle | infektionen | infektionszahlen | infizierte | infizierten | fallzahlen;

root $input;
$input = $alle {$ = "all"} | $woche {$ = "week"};
$alle = gesamt | gesamter zeitraum | gesamten zeitraum | bisher | bisherige | seit beginn;
$woche = sieben tage | woche;

root $input;
$input = $gesamt {$ = "all"} | $einwohnerzahl {$ = "pop"} | $einhundertk {$ = "hundredk"};
$gesamt = alle | anzahl| insgesamt | absolut | keine relation | summe;
$einwohnerzahl = einwohner | einwohnerzahl;
$einhundertk = hunderttausend | einhunderttausend;

att.value:
struct attr = get(get(get(apiReturn, "features"), 0), "attributes");
string landkreis = get(RESULTCovid19Info, "Landkreis");
string casesDeaths = get(RESULTCovid19Info, "FallTyp");
string time = get(RESULTCovid19Info, "Zeitraum");
string relative = get(RESULTCovid19Info, "RelativZu");
string dataName = "";
real ewz = real(get(attr, "EWZ"));
real base = 0;
real result = 0.0;
int rounded = 0;
answer = "Die ";

if (casesDeaths ==  "deaths")
{
	if (contains(time, "all"))
	{
		answer = answer + "gesamte Anzahl der Todesfälle in ";
		dataName = "deaths";
	}
	if (contains(time, "week"))
	{
		answer = answer + "Anzahl der Todesfälle der vergangenen Woche in ";
		dataName = "death7_lk";	
	}
}

if (casesDeaths == "cases")
{
	if (time == "all")
	{
		answer = answer + "gesamte Anzahl der Infektionen in ";
		dataName = "cases";
	}
	if (time == "week")
	{
		answer = answer + "Anzahl der Infektionen innerhalb der letzten sieben Tage in ";
		dataName = "cases7_lk";	
	}
}

answer = answer + landkreis + " ";

base = real(get(attr, dataName));

switch(relative)
{
	case "all":
		result = base;
	break;
	case "pop":
		answer = answer + "relativ zur Einwohnerzahl ";
		result = base / ewz;
	break;
	case "hundredk":
		answer = answer + "pro hunderttausend ";
		result = base / ewz * 100000.0;
	break;
}

rounded = round(result);

answer = answer + "beträgt " + str(rounded);
